import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-chat-interface',
  templateUrl: './chat-interface.component.html',
  styleUrls: ['./chat-interface.component.scss']
})
export class ChatInterfaceComponent {
  showChatGPT: boolean = true; // Initially show the ChatGPT-like section
  userInput: string | undefined;
  preMadeQuestions = [
    { title: 'What are my rights during the layoff period?' },
    { title: 'If I find a new job, do I need to let my current employer know?' },
    { title: 'Can my employer fire me', subtitle: 'during the layoff period?' },
    { title: 'Explain Change Negotiations', subtitle: 'as if I’m a 5-year old' }
  ];


  constructor(private http: HttpClient) {}
 // Function to toggle to chat interface when the conversation starts
  startChat() {
  this.showChatGPT = false;
  // Add any other logic related to starting the chat
  }
  sendUserInput(userInput: string) {
    // Call the GPT API with user input¨
    console.log(userInput);
    this.showChatGPT = false;
    this.userInput = userInput;
    // this.http.post('YOUR_GPT_API_ENDPOINT', { message: userInput }).subscribe((response: any) => {
    //   console.log(response); // Handle the response from the GPT API
    //   // Display the response in the chat interface messages section
    // });
  }

  sendQuestion(input: string) {
    const question = input;
    this.sendUserInput(question); // Call the method to send the question to the API
  }

}
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <title>Chat GPT with Search Box</title>
    <link href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css" rel="stylesheet">
    <!-- Include your custom CSS or Bootstrap theme here -->
    <style>
        /* Your custom CSS can go here */
    </style>
</head>

<body>

    <div class="container mt-5">
        <div class="row justify-content-center">
            <div class="col-md-8">
                <div class="card text-center">
                    <div class="card-body">
                        <div class="d-flex flex-column align-items-center">
                            <div class="chat-gpt-circle bg-primary text-white d-flex align-items-center justify-content-center rounded-circle mb-3">
                                <!-- Your circular icon content goes here -->
                                <span style="font-size: 36px;">GPT</span>
                            </div>
                            <h2 class="card-title">Change Negotiations GPT</h2>
                            <p class="card-text">Custom GPT tool designed to give you more information about the Change Negotiations journey in Finland crafted by people with firsthand experience. We’re in this together.</p>

                            <!-- Pre-made questions in two rows -->
                            <div class="row">
                                <div class="col-md-6 mb-3">
                                    <div class="question-box bg-light border rounded p-3" onclick="sendQuestion('What are my rights during the layoff period?')">
                                        <div class="question-content">
                                            <div class="title">What are my rights</div>
                                            <div class="subtitle">during the layoff period?</div>
                                        </div>
                                    </div>
                                </div>
                                <div class="col-md-6 mb-3">
                                    <div class="question-box bg-light border rounded p-3" onclick="sendQuestion('If I find a new job do I need to let my current employer know?')">
                                        <div class="question-content">
                                            <div class="title">If I find a new job</div>
                                            <div class="subtitle">do I need to let my current employer know?</div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="row">
                                <div class="col-md-6 mb-3">
                                    <div class="question-box bg-light border rounded p-3" onclick="sendQuestion('Can my employer fire me during layoff period?')">
                                        <div class="question-content">
                                            <div class="title">Can my employer fire me</div>
                                            <div class="subtitle">during the layoff period?</div>
                                        </div>
                                    </div>
                                </div>
                                <div class="col-md-6 mb-3">
                                    <div class="question-box bg-light border rounded p-3" click()="sendQuestion(Explain change negotiations as if I’m a five year-old')">
                                        <div class="question-content">
                                            <div class="title">Explain Change Negotiations</div>
                                            <div class="subtitle">as if I’m a 5-year old</div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <!-- Search Box -->
                            <div class="input-group mt-4">
                                <input type="text" class="form-control" placeholder="Search pre-made questions...">
                                <div class="input-group-append">
                                    <button class="btn btn-outline-secondary" type="button">Search</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>

    <!-- Bootstrap JS -->
    <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>

    <!-- Your custom script -->
    <script>
        function sendQuestion(question) {
            // Implement your logic to handle the selected question
            console.log("Selected question: " + question);
            // You can perform actions like sending the question to the chat system, etc.
        }
    </script>

</body>

</html>
